import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { PWAInstallButton } from "@/components/PWAInstallButton";

export const ChildLink = (): JSX.Element => {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const [childName, setChildName] = useState("");
  const [code, setCode] = useState("");
  const [method, setMethod] = useState<"qr" | "code">("code");

  const linkMutation = useMutation({
    mutationFn: async () => {
      const res = await fetch("/api/child/link", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ childName, code }),
      });
      if (!res.ok) throw new Error("Invalid code");
      return res.json();
    },
    onSuccess: (data) => {
      localStorage.setItem("childToken", data.token);
      localStorage.setItem("childId", data.childId);
      navigate("/child-games");
    },
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-400 to-green-600 flex items-center justify-center px-4">
      <div className="w-full max-w-md">
        {/* Back Button & Install */}
        <div className="flex justify-between items-center mb-8">
          <button
            onClick={() => navigate("/")}
            className="text-white flex items-center gap-2 hover:opacity-80"
          >
            ‚Üê {t("back")}
          </button>
          <PWAInstallButton 
            variant="default" 
            size="default"
            showText={true}
            className="bg-cyan-500 hover:bg-cyan-600 text-white rounded-full px-4 py-2 font-semibold shadow-md"
          />
        </div>

        {/* Card */}
        <div className="bg-white rounded-3xl p-8 shadow-2xl">
          <h1 className="text-3xl font-bold text-gray-800 mb-2 text-center">
            {t("childLinking")}
          </h1>
          <p className="text-gray-600 text-center mb-6">
            {t("linkAccountWithParent")}
          </p>

          <form
            onSubmit={(e) => {
              e.preventDefault();
              linkMutation.mutate();
            }}
            className="space-y-4"
          >
            {/* Name */}
            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">
                {t("childName")}
              </label>
              <input
                type="text"
                value={childName}
                onChange={(e) => setChildName(e.target.value)}
                placeholder={t("enterYourName")}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:border-green-400 text-gray-900 bg-white"
                required
              />
            </div>

            {/* Method Tabs */}
            <div className="flex gap-4 border-b-2 mb-4">
              <button
                type="button"
                onClick={() => setMethod("code")}
                className={`flex-1 pb-3 font-bold text-center ${
                  method === "code"
                    ? "text-green-500 border-b-2 border-green-500 -mb-2"
                    : "text-gray-500"
                }`}
              >
                {t("code")}
              </button>
              <button
                type="button"
                onClick={() => setMethod("qr")}
                className={`flex-1 pb-3 font-bold text-center ${
                  method === "qr"
                    ? "text-green-500 border-b-2 border-green-500 -mb-2"
                    : "text-gray-500"
                }`}
              >
                {t("qrCode")}
              </button>
            </div>

            {/* Code Input */}
            {method === "code" && (
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">
                  {t("enterCode")}
                </label>
                <input
                  type="text"
                  value={code}
                  onChange={(e) => setCode(e.target.value.toUpperCase())}
                  placeholder="PARENT123"
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:border-green-400 text-center text-2xl tracking-widest font-mono text-gray-900 bg-white"
                  maxLength={10}
                  required
                />
              </div>
            )}

            {/* QR Upload */}
            {method === "qr" && (
              <div className="text-center py-8 border-2 border-dashed border-green-300 rounded-lg">
                <p className="text-gray-600 mb-4">{t("scanQR")}</p>
                <div className="text-4xl">üì∑</div>
                <button
                  type="button"
                  className="mt-4 px-4 py-2 bg-green-500 hover:bg-green-600 text-white font-bold rounded-lg"
                >
                  {t("uploadQRImage")}
                </button>
              </div>
            )}

            <button
              type="submit"
              disabled={linkMutation.isPending || !childName || !code}
              className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-3 rounded-lg transition-all disabled:opacity-50"
            >
              {linkMutation.isPending ? t("linking") : t("link")}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

