import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { PWAInstallButton } from "@/components/PWAInstallButton";

export const ParentAuth = (): JSX.Element => {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const [isLogin, setIsLogin] = useState(true);
  const [usePhone, setUsePhone] = useState(false);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [error, setError] = useState("");

  const authMutation = useMutation({
    mutationFn: async () => {
      if (usePhone) {
        const endpoint = isLogin ? "/api/auth/login-phone" : "/api/auth/register-phone";
        const res = await fetch(endpoint, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(
            isLogin ? { phoneNumber: phone, password } : { phoneNumber: phone, password, name }
          ),
        });
        if (!res.ok) {
          const err = await res.json();
          throw new Error(err.message);
        }
        return res.json();
      } else {
        const endpoint = isLogin ? "/api/auth/login" : "/api/auth/register";
        const res = await fetch(endpoint, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(
            isLogin ? { email, password } : { email, password, name }
          ),
        });
        if (!res.ok) {
          const err = await res.json();
          throw new Error(err.message);
        }
        return res.json();
      }
    },
    onSuccess: (data) => {
      if (data.requiresOtp) {
        localStorage.setItem("otpEmail", data.email || data.phone);
        navigate("/otp");
      } else {
        localStorage.setItem("token", data.token);
        localStorage.setItem("userId", data.userId);
        navigate("/parent-dashboard");
      }
    },
    onError: (err: any) => {
      setError(err.message);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    authMutation.mutate();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-400 to-blue-600 flex items-center justify-center px-4">
      <div className="w-full max-w-md">
        <div className="flex justify-between items-center mb-8">
          <button
            onClick={() => navigate("/")}
            className="text-white flex items-center gap-2 hover:opacity-80"
          >
            ‚Üê {t("back")}
          </button>
          <PWAInstallButton 
            variant="default" 
            size="default"
            showText={true}
            className="bg-cyan-500 hover:bg-cyan-600 text-white rounded-full px-4 py-2 font-semibold shadow-md"
          />
        </div>

        <div className="bg-white rounded-3xl p-8 shadow-2xl">
          <h1 className="text-3xl font-bold text-gray-800 mb-2 text-center">
            {isLogin ? t("parentLogin") : t("registerNewParent")}
          </h1>

          {/* Email/Phone Toggle */}
          <div className="flex gap-2 mb-6 bg-gray-100 p-2 rounded-lg">
            <button
              type="button"
              onClick={() => {
                setUsePhone(false);
                setError("");
              }}
              className={`flex-1 py-2 px-4 rounded-md font-bold transition-all ${
                !usePhone ? "bg-blue-500 text-white" : "text-gray-700"
              }`}
            >
              üìß ÿßŸÑÿ®ÿ±ŸäÿØ
            </button>
            <button
              type="button"
              onClick={() => {
                setUsePhone(true);
                setError("");
              }}
              className={`flex-1 py-2 px-4 rounded-md font-bold transition-all ${
                usePhone ? "bg-blue-500 text-white" : "text-gray-700"
              }`}
            >
              üì± ÿßŸÑŸáÿßÿ™ŸÅ
            </button>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            {!isLogin && (
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">
