import { useState } from "react";
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/contexts/ThemeContext";
import { SEOProvider } from "@/components/SEOProvider";
import { ChildAppWrapper } from "@/components/ChildAppWrapper";
import { SplashScreen } from "@/components/SplashScreen";
import { PWAInstallGate } from "@/components/PWAInstallGate";
import { AnimatePresence, motion } from "framer-motion";
import NotFound from "@/pages/not-found";

import { Home } from "@/pages/Home";
import { ParentAuth } from "@/pages/ParentAuth";
import { ChildLink } from "@/pages/ChildLink";
import { ParentDashboard } from "@/pages/ParentDashboard";
import { ChildGames } from "@/pages/ChildGames";
import { CreateTask } from "@/pages/CreateTask";
import { ParentStore } from "@/pages/ParentStore";
import { ChildStore } from "@/pages/ChildStore";
import { ChildGifts } from "@/pages/ChildGifts";
import { ChildNotifications } from "@/pages/ChildNotifications";
import { Privacy } from "@/pages/Privacy";
import { Terms } from "@/pages/Terms";
import { Settings } from "@/pages/Settings";
import { Wallet } from "@/pages/Wallet";
import { Subjects } from "@/pages/Subjects";
import { Notifications } from "@/pages/Notifications";
import { AdminDashboard } from "@/pages/AdminDashboard";
import { AdminAuth } from "@/pages/AdminAuth";
import { OTPVerification } from "@/pages/OTPVerification";
import { PrivacyPolicy } from "@/pages/PrivacyPolicy";
import { AccessibilityPolicy } from "@/pages/AccessibilityPolicy";
import { AssignTask } from "@/pages/AssignTask";
import { ForgotPassword } from "@/pages/ForgotPassword";

function WrappedChildGames() {
  return <ChildAppWrapper><ChildGames /></ChildAppWrapper>;
}
function WrappedChildStore() {
  return <ChildAppWrapper><ChildStore /></ChildAppWrapper>;
}
function WrappedChildGifts() {
  return <ChildAppWrapper><ChildGifts /></ChildAppWrapper>;
}
function WrappedChildNotifications() {
  return <ChildAppWrapper><ChildNotifications /></ChildAppWrapper>;
}

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/parent-auth" component={ParentAuth} />
      <Route path="/child-link" component={ChildLink} />
      <Route path="/parent-dashboard" component={ParentDashboard} />
      <Route path="/parent-store" component={ParentStore} />
      <Route path="/wallet" component={Wallet} />
      <Route path="/notifications" component={Notifications} />
      <Route path="/subjects" component={Subjects} />
      <Route path="/admin" component={AdminAuth} />
      <Route path="/admin-dashboard" component={AdminDashboard} />
      <Route path="/otp" component={OTPVerification} />
      <Route path="/forgot-password" component={ForgotPassword} />
      <Route path="/child-games" component={WrappedChildGames} />
      <Route path="/child-store" component={WrappedChildStore} />
      <Route path="/child-gifts" component={WrappedChildGifts} />
      <Route path="/child-notifications" component={WrappedChildNotifications} />
      <Route path="/create-task" component={CreateTask} />
      <Route path="/assign-task" component={AssignTask} />
      <Route path="/privacy" component={Privacy} />
      <Route path="/privacy-policy" component={PrivacyPolicy} />
      <Route path="/accessibility" component={AccessibilityPolicy} />
      <Route path="/terms" component={Terms} />
      <Route path="/settings" component={Settings} />
      <Route component={NotFound} />
    </Switch>
  );
}

function AppContent() {
  const [showSplash, setShowSplash] = useState(true);

  const handleSplashComplete = () => {
    setShowSplash(false);
  };

  return (
    <>
      <AnimatePresence mode="wait">
        {showSplash && (
          <SplashScreen onComplete={handleSplashComplete} duration={1500} />
        )}
      </AnimatePresence>

      {!showSplash && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.3 }}
          className="min-h-screen"
        >
          <Router />
        </motion.div>
      )}

      <Toaster />
    </>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <SEOProvider>
          <TooltipProvider>
            <PWAInstallGate>
              <AppContent />
            </PWAInstallGate>
          </TooltipProvider>
        </SEOProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

