import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { useTheme } from "@/contexts/ThemeContext";

export const LanguageSelector: React.FC = () => {
  const { i18n } = useTranslation();
  const { isDark } = useTheme();
  const [isOpen, setIsOpen] = useState(false);

  const languages = [
    { code: "ar", name: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©", flag: "üá∏üá¶", nativeName: "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©" },
    { code: "en", name: "English", flag: "üá¨üáß", nativeName: "English" },
    { code: "fr", name: "Fran√ßais", flag: "üá´üá∑", nativeName: "Fran√ßais" },
    { code: "es", name: "Espa√±ol", flag: "üá™üá∏", nativeName: "Espa√±ol" },
    { code: "de", name: "Deutsch", flag: "üá©üá™", nativeName: "Deutsch" },
    { code: "zh", name: "‰∏≠Êñá", flag: "üá®üá≥", nativeName: "‰∏≠Êñá" },
    { code: "ja", name: "Êó•Êú¨Ë™û", flag: "üáØüáµ", nativeName: "Êó•Êú¨Ë™û" },
    { code: "pt", name: "Portugu√™s", flag: "üáµüáπ", nativeName: "Portugu√™s" },
  ];

  const currentLang = languages.find((l) => l.code === i18n.language) || languages[1];

  return (
    <div className="relative">
      {/* Main Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={`flex items-center gap-2 px-4 py-2 rounded-lg font-semibold transition-all duration-200 shadow-md hover:shadow-lg ${
          isDark
            ? "bg-gray-700 hover:bg-gray-600 text-white border border-gray-600"
            : "bg-white hover:bg-gray-100 text-gray-800 border-2 border-purple-300"
        }`}
      >
        <span className="text-lg">{currentLang.flag}</span>
        <span className="hidden sm:inline text-sm font-bold">{currentLang.nativeName}</span>
        <span className={`text-xs transition-transform ml-1 ${isOpen ? "rotate-180" : ""}`}>
          ‚¨áÔ∏è
        </span>
      </button>

      {/* Dropdown Menu */}
      {isOpen && (
        <div
          className={`absolute top-12 right-0 min-w-max rounded-xl shadow-2xl z-50 overflow-hidden border-2 ${
            isDark
              ? "bg-gray-800 border-gray-600"
              : "bg-white border-purple-300"
          }`}
        >
          {languages.map((lang) => (
            <button
              key={lang.code}
              onClick={() => {
                i18n.changeLanguage(lang.code);
                setIsOpen(false);
              }}
              className={`w-full px-4 py-3 text-left flex items-center gap-3 transition-all ${
                i18n.language === lang.code
                  ? isDark
                    ? "bg-purple-600 text-white font-bold"
                    : "bg-purple-100 text-purple-800 font-bold"
                  : isDark
                    ? "text-gray-200 hover:bg-gray-700"
                    : "text-gray-800 hover:bg-gray-50"
              }`}
            >
              <span className="text-xl">{lang.flag}</span>
              <div>
                <p className="font-semibold text-sm">{lang.name}</p>
                <p className="text-xs opacity-70">{lang.nativeName}</p>
              </div>
              {i18n.language === lang.code && (
                <span className="ml-auto text-lg font-bold">‚úÖ</span>
              )}
            </button>
          ))}
        </div>
      )}

      {/* Backdrop to close dropdown */}
      {isOpen && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
};

