import { useTranslation } from "react-i18next";
import { useQuery } from "@tanstack/react-query";
import { MandatoryTaskModal } from "@/components/MandatoryTaskModal";
import { PWAInstallButton } from "@/components/PWAInstallButton";

export const ChildGames = (): JSX.Element => {
  const { t } = useTranslation();
  const token = localStorage.getItem("childToken");

  const { data: games } = useQuery({
    queryKey: ["games"],
    queryFn: async () => {
      const res = await fetch("/api/games");
      return res.json();
    },
  });

  const { data: tasks } = useQuery({
    queryKey: ["child-tasks"],
    queryFn: async () => {
      const res = await fetch("/api/child/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    enabled: !!token,
  });

  const { data: childInfo } = useQuery({
    queryKey: ["child-info"],
    queryFn: async () => {
      const res = await fetch("/api/child/info", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    enabled: !!token,
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-400 to-purple-600 p-4">
      {/* Mandatory Task Modal - shows if there are pending tasks */}
      {childInfo?.id && <MandatoryTaskModal childId={childInfo.id} />}

      {/* Header with Store Link */}
      <div className="max-w-6xl mx-auto mb-8 flex flex-wrap justify-between items-center gap-4">
        <p className="text-white text-3xl font-bold">
          ‚≠ê {t("yourPoints")}: {childInfo?.totalPoints || 0}
        </p>
        <div className="flex items-center gap-3">
          <PWAInstallButton 
            variant="outline" 
            size="sm" 
            showText={false}
            className="bg-cyan-500 hover:bg-cyan-600 text-white border-cyan-400 rounded-xl px-4 py-3"
          />
          <button
            onClick={() => window.location.href = "/child-notifications"}
            className="relative px-4 py-3 bg-orange-500 hover:bg-orange-600 text-white font-bold rounded-xl shadow-lg transition-all"
            data-testid="button-child-notifications"
          >
            üîî
            {tasks && tasks.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs font-bold px-2 py-1 rounded-full">
                {tasks.length}
              </span>
            )}
          </button>
          <button
            onClick={() => window.location.href = "/child-store"}
            className="px-6 py-3 bg-pink-500 hover:bg-pink-600 text-white font-bold rounded-xl shadow-lg transition-all"
            data-testid="button-child-store"
          >
