import { QueryClient, QueryFunction } from "@tanstack/react-query";
import { getCachedApiResponse, setCachedApiResponse, getAuthData } from "./cache-storage";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const authData = await getAuthData();
  const headers: HeadersInit = data ? { "Content-Type": "application/json" } : {};

  if (authData?.token) {
    (headers as Record<string, string>)["Authorization"] = `Bearer ${authData.token}`;
  }

  const res = await fetch(url, {
    method,
    headers,
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";

// Cacheable API patterns
const CACHEABLE_PATTERNS = [
  '/api/subjects',
  '/api/seo-settings',
  '/api/theme-settings',
  '/api/products',
  '/api/flash-games',
  '/api/children'
];

function isCacheableUrl(url: string): boolean {
  // Extract base path without query params for matching
  const basePath = url.split('?')[0];
  return CACHEABLE_PATTERNS.some(pattern => basePath.startsWith(pattern) || basePath.includes(pattern));
}

export function getQueryFn<T>(options: {
  on401: UnauthorizedBehavior;
}): QueryFunction<T> {
  const { on401: unauthorizedBehavior } = options;

  return async ({ queryKey }) => {
    // Handle both string arrays and simple strings as query keys
    const url = Array.isArray(queryKey) 
      ? (queryKey.length === 1 ? String(queryKey[0]) : queryKey.join('/'))
      : String(queryKey);
    const isCacheable = isCacheableUrl(url);

    // Try to get from cache first for cacheable URLs
    if (isCacheable) {
      const cachedData = await getCachedApiResponse<unknown>(url);
      if (cachedData !== null) {
        // Update cache in background
        fetchAndUpdateCache(url);
        return cachedData as T;
      }
    }

    // Fetch from network
    const authData = await getAuthData();
    const headers: HeadersInit = {};

    if (authData?.token) {
      (headers as Record<string, string>)["Authorization"] = `Bearer ${authData.token}`;
    }

    const res = await fetch(url, {
      credentials: "include",
      headers,
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    const data = await res.json();

    // Cache the response for cacheable URLs
    if (isCacheable && res.ok) {
      await setCachedApiResponse(url, data);
    }

    return data;
  };
}

// Background fetch and cache update
async function fetchAndUpdateCache(url: string): Promise<void> {
  try {
    const authData = await getAuthData();
    const headers: HeadersInit = {};

    if (authData?.token) {
      (headers as Record<string, string>)["Authorization"] = `Bearer ${authData.token}`;
    }

    const res = await fetch(url, {
      credentials: "include",
      headers,
    });

    if (res.ok) {
      const data = await res.json();
      await setCachedApiResponse(url, data);
    }
  } catch (error) {
    console.log('[QueryClient] Background cache update failed:', error);
  }
}

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: 5 * 60 * 1000, // 5 minutes
      gcTime: 30 * 60 * 1000, // 30 minutes (was cacheTime)
      retry: 1,
      retryDelay: 1000,
    },
    mutations: {
      retry: false,
    },
  },
});

