import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { MandatoryTaskModal } from "@/components/MandatoryTaskModal";

export const ChildStore = (): JSX.Element => {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const token = localStorage.getItem("childToken");
  const [selectedProduct, setSelectedProduct] = useState<any>(null);
  const [shippingAddress, setShippingAddress] = useState("");

  const { data: products } = useQuery({
    queryKey: ["child-store"],
    queryFn: async () => {
      const res = await fetch("/api/child/store", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
  });

  const { data: childInfo } = useQuery({
    queryKey: ["child-info"],
    queryFn: async () => {
      const res = await fetch("/api/child/info", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
  });

  const buyMutation = useMutation({
    mutationFn: async ({ orderId, shippingAddress }: any) => {
      const res = await fetch("/api/child/buy-product", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ orderId, shippingAddress }),
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.message || "ÿπÿØÿØ ÿßŸÑŸÜŸÇÿßÿ∑ ÿ∫Ÿäÿ± ŸÉÿßŸÅŸä");
      }
      return res.json();
    },
    onSuccess: () => {
      alert("ÿ™ŸÖ ÿßŸÑÿ¥ÿ±ÿßÿ°! ÿ≥Ÿäÿ™ŸÖ ÿ¥ÿ≠ŸÜ ÿßŸÑŸÖŸÜÿ™ÿ¨ ŸÇÿ±Ÿäÿ®ÿßŸã");
      setSelectedProduct(null);
    },
    onError: (error: any) => {
      alert(error.message);
    },
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-300 to-pink-500 p-6">
      {/* Mandatory Task Modal */}
      {childInfo?.id && <MandatoryTaskModal childId={childInfo.id} />}

      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="flex flex-wrap justify-between items-center gap-4 mb-8">
          <p className="text-white text-3xl font-bold">
            ‚≠ê ÿßŸÑŸÜŸÇÿßÿ∑: {childInfo?.totalPoints || 0}
          </p>
          <div className="flex items-center gap-3">
            <button
              onClick={() => navigate("/child-notifications")}
              className="relative px-4 py-3 bg-orange-500 hover:bg-orange-600 text-white font-bold rounded-xl shadow-lg transition-all"
              data-testid="button-child-notifications"
            >
              üîî
            </button>
            <button
              onClick={() => navigate("/child-games")}
              className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white font-bold rounded-lg"
            >
              ‚Üê ÿ±ÿ¨Ÿàÿπ
            </button>
          </div>
        </div>

        {/* Store Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {products?.map((product: any) => (
            <div
              key={product.id}
              className="bg-white rounded-2xl p-6 shadow-lg hover:shadow-xl transition-all cursor-pointer transform hover:-translate-y-1"
            >
              <div className="mb-4">
                <div className="w-full h-32 bg-pink-100 rounded-lg flex items-center justify-center">
                  <div className="text-5xl">üéÅ</div>
