import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { parents, children, parentChild, tasks, taskResults, flashGames, themeSettings, subjects, templateTasks, orders, products, childPurchases, parentWallet, paymentMethods, deposits, notifications, otpCodes, admins, siteSettings } from "../shared/schema";
import { eq, and } from "drizzle-orm";
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";
import envDashboard from "./env-dashboard";

const JWT_SECRET = process.env.JWT_SECRET || "classify-app-2025-secret";
if (!process.env.JWT_SECRET && process.env.NODE_ENV === "production") {
  console.error("ERROR: JWT_SECRET must be set in production!");
  process.exit(1);
}

export async function registerRoutes(app: Express): Promise<Server> {
  const db = storage.db;

  // Auth Middleware
  const authMiddleware = (req: any, res: any, next: any) => {
    const token = req.headers.authorization?.split(" ")[1];
    if (!token) return res.status(401).json({ message: "Unauthorized" });
    try {
      req.user = jwt.verify(token, JWT_SECRET);
      next();
    } catch {
      res.status(401).json({ message: "Invalid token" });
    }
  };

  // Admin Dashboard for environment management
  app.use("/admin/env", envDashboard);

  // Parent Register
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { email, password, name } = req.body;

      // Validation
      if (!email || !password || !name) {
        return res.status(400).json({ message: "Email, password, and name are required" });
      }
      if (password.length < 6) {
        return res.status(400).json({ message: "Password must be at least 6 characters" });
      }
      if (!email.includes("@")) {
        return res.status(400).json({ message: "Invalid email format" });
      }

      // Check if email exists
      const existing = await db.select().from(parents).where(eq(parents.email, email));
      if (existing[0]) {
        return res.status(400).json({ message: "Email already registered" });
      }

      const hashedPassword = await bcrypt.hash(password, 10);
      const uniqueCode = Math.random().toString(36).substring(2, 8).toUpperCase();

      const result = await db.insert(parents).values({
        email,
        password: hashedPassword,
        name,
        uniqueCode,
      }).returning();

      const token = jwt.sign({ userId: result[0].id, type: "parent" }, JWT_SECRET, { expiresIn: "30d" });
      res.json({ token, userId: result[0].id, uniqueCode });
    } catch (error: any) {
      res.status(500).json({ message: "Registration failed" });
    }
  });

  // Parent Login
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email and password are required" });
      }

      const result = await db.select().from(parents).where(eq(parents.email, email));
      if (!result[0]) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const passwordMatch = await bcrypt.compare(password, result[0].password);
      if (!passwordMatch) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Send OTP instead of direct token
      const code = Math.floor(100000 + Math.random() * 900000).toString();
      await db.insert(otpCodes).values({
        parentId: result[0].id,
        code,
        method: "email",
        destination: email,
        expiresAt: new Date(Date.now() + 5 * 60 * 1000),
      });

      console.log(`OTP for ${email}: ${code}`);
      res.json({ requiresOtp: true, email });
    } catch (error: any) {
      res.status(500).json({ message: "Login failed" });
    }
  });

  // Child Link
  app.post("/api/child/link", async (req, res) => {
    try {
      const { childName, code } = req.body;

      if (!childName || !code) {
        return res.status(400).json({ message: "Child name and parent code are required" });
      }

      // Find parent by unique code
      const parentResult = await db.select().from(parents).where(eq(parents.uniqueCode, code.toUpperCase()));
      if (!parentResult[0]) {
        return res.status(400).json({ message: "Invalid parent code" });
      }

      // Create child
      const childResult = await db.insert(children).values({
        name: childName,
      }).returning();

      // Link parent and child
      await db.insert(parentChild).values({
        parentId: parentResult[0].id,
        childId: childResult[0].id,
      });

      const token = jwt.sign({ childId: childResult[0].id, type: "child" }, JWT_SECRET, { expiresIn: "30d" });
      res.json({ token, childId: childResult[0].id });
    } catch (error: any) {
      res.status(500).json({ message: "Linking failed" });
    }
  });

  // Forgot Password - Send reset OTP
  app.post("/api/auth/forgot-password", async (req, res) => {
    try {
      const { email } = req.body;
      if (!email) {
        return res.status(400).json({ message: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿ∑ŸÑŸàÿ®" });
      }

      const parent = await db.select().from(parents).where(eq(parents.email, email));
      if (!parent[0]) {
        return res.status(400).json({ message: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ŸÖÿ≥ÿ¨ŸÑ" });
