    }
  });

  // Verify reset OTP
  app.post("/api/auth/verify-reset-otp", async (req, res) => {
    try {
      const { email, code } = req.body;
      if (!email || !code) {
        return res.status(400).json({ message: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸàÿßŸÑÿ±ŸÖÿ≤ ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ" });
      }

      const otpRecord = await db
        .select()
        .from(otpCodes)
        .where(and(
          eq(otpCodes.destination, email),
          eq(otpCodes.code, code),
          eq(otpCodes.isUsed, false)
        ));

      if (!otpRecord[0]) {
        return res.status(400).json({ message: "ÿ±ŸÖÿ≤ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠" });
      }
      if (new Date() > otpRecord[0].expiresAt) {
        return res.status(400).json({ message: "ÿßŸÜÿ™Ÿáÿ™ ÿµŸÑÿßÿ≠Ÿäÿ© ÿßŸÑÿ±ŸÖÿ≤" });
      }

      res.json({ success: true, message: "ÿ™ŸÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ±ŸÖÿ≤" });
    } catch (error: any) {
