      res.status(500).json({ message: "ŸÅÿ¥ŸÑ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ±ŸÖÿ≤" });
    }
  });

  // Reset password
  app.post("/api/auth/reset-password", async (req, res) => {
    try {
      const { email, code, newPassword } = req.body;
      if (!email || !code || !newPassword) {
        return res.status(400).json({ message: "ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ ŸÖÿ∑ŸÑŸàÿ®ÿ©" });
      }

      if (newPassword.length < 6) {
        return res.status(400).json({ message: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ" });
      }

      const otpRecord = await db
        .select()
        .from(otpCodes)
        .where(and(
          eq(otpCodes.destination, email),
          eq(otpCodes.code, code),
          eq(otpCodes.isUsed, false)
        ));

      if (!otpRecord[0]) {
        return res.status(400).json({ message: "ÿ±ŸÖÿ≤ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠" });
      }
      if (new Date() > otpRecord[0].expiresAt) {
        return res.status(400).json({ message: "ÿßŸÜÿ™Ÿáÿ™ ÿµŸÑÿßÿ≠Ÿäÿ© ÿßŸÑÿ±ŸÖÿ≤" });
      }

      // Mark OTP as used
      await db.update(otpCodes).set({ isUsed: true }).where(eq(otpCodes.id, otpRecord[0].id));

      // Update password
      const hashedPassword = await bcrypt.hash(newPassword, 10);
      await db.update(parents).set({ password: hashedPassword }).where(eq(parents.email, email));

      res.json({ success: true, message: "ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ®ŸÜÿ¨ÿßÿ≠" });
    } catch (error: any) {
      console.error("Reset password error:", error);
      res.status(500).json({ message: "ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±" });
    }
  });

  // Update Parent Profile (ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™)
  app.post("/api/parent/profile/update", authMiddleware, async (req: any, res) => {
    try {
      const { name, email } = req.body;

      if (!name && !email) {
        return res.status(400).json({ message: "At least one field required" });
      }

      if (email) {
        const existing = await db.select().from(parents).where(eq(parents.email, email));
        if (existing[0] && existing[0].id !== req.user.userId) {
          return res.status(400).json({ message: "Email already in use" });
        }
      }

      const result = await db.update(parents)
        .set({ name: name || undefined, email: email || undefined })
        .where(eq(parents.id, req.user.userId))
        .returning();

      res.json(result[0]);
    } catch (error: any) {
      res.status(500).json({ message: "Update failed" });
    }
  });

  // Change Password (ÿ™ÿ∫ŸäŸäÿ± ŸÉŸÑŸÖÿ© ÿßŸÑÿ≥ÿ±)
  app.post("/api/parent/profile/change-password", authMiddleware, async (req: any, res) => {
    try {
      const { oldPassword, newPassword } = req.body;

      if (!oldPassword || !newPassword) {
        return res.status(400).json({ message: "Both passwords required" });
      }

      if (newPassword.length < 6) {
        return res.status(400).json({ message: "Password must be at least 6 characters" });
      }

      const result = await db.select().from(parents).where(eq(parents.id, req.user.userId));
      if (!result[0]) return res.status(404).json({ message: "Parent not found" });

      const passwordMatch = await bcrypt.compare(oldPassword, result[0].password);
      if (!passwordMatch) {
