      }

      const hashedPassword = await bcrypt.hash(newPassword, 10);
      await db.update(parents)
        .set({ password: hashedPassword })
        .where(eq(parents.id, req.user.userId));

      res.json({ success: true, message: "Password changed successfully" });
    } catch (error: any) {
      res.status(500).json({ message: "Password change failed" });
    }
  });

  // Get Parent's Info (uniqueCode, etc)
  app.get("/api/parent/info", authMiddleware, async (req: any, res) => {
    try {
      const result = await db.select().from(parents).where(eq(parents.id, req.user.userId));
      if (!result[0]) return res.status(404).json({ message: "Parent not found" });

      res.json({
