      res.json(result);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Get Pending Tasks for Child
  app.get("/api/child/tasks", authMiddleware, async (req: any, res) => {
    try {
      const result = await db
        .select()
        .from(tasks)
        .where(and(
          eq(tasks.childId, req.user.childId),
          eq(tasks.status, "pending")
        ));
      res.json(result);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Submit Task Answer
  app.post("/api/child/submit-task", authMiddleware, async (req: any, res) => {
    try {
      const { taskId, selectedAnswerId } = req.body;

      const task = await db.select().from(tasks).where(eq(tasks.id, taskId));
      if (!task[0]) return res.status(400).json({ message: "Task not found" });

      const answer = task[0].answers.find((a: any) => a.id === selectedAnswerId);
      const isCorrect = answer?.isCorrect || false;
      const pointsEarned = isCorrect ? task[0].pointsReward : 0;

      await db.insert(taskResults).values({
        taskId,
        childId: req.user.childId,
        selectedAnswerId,
        isCorrect,
        pointsEarned,
      });

      // Mark task as completed
      await db.update(tasks).set({ status: "completed" }).where(eq(tasks.id, taskId));

      if (isCorrect) {
        const child = await db.select().from(children).where(eq(children.id, req.user.childId));
        await db.update(children).set({
          totalPoints: (child[0]?.totalPoints || 0) + pointsEarned,
        }).where(eq(children.id, req.user.childId));
      }

      res.json({ isCorrect, pointsEarned });
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Get Pending Tasks for Mandatory Modal
  app.get("/api/child/pending-tasks", authMiddleware, async (req: any, res) => {
    try {
      const result = await db
        .select()
        .from(tasks)
        .where(and(
          eq(tasks.childId, req.user.childId),
          eq(tasks.status, "pending")
        ))
        .orderBy(tasks.createdAt);
      res.json(result);
    } catch (error: any) {
      res.status(400).json({ message: error.message });
    }
  });

  // Answer Task from Mandatory Modal
  app.post("/api/child/answer-task", authMiddleware, async (req: any, res) => {
    try {
      const { taskId, answerId } = req.body;

      const task = await db.select().from(tasks).where(eq(tasks.id, taskId));
      if (!task[0]) return res.status(400).json({ message: "Task not found" });
      if (task[0].childId !== req.user.childId) {
        return res.status(403).json({ message: "Unauthorized" });
      }

      const answer = task[0].answers.find((a: any) => a.id === answerId);
      const isCorrect = answer?.isCorrect || false;
      const pointsEarned = isCorrect ? task[0].pointsReward : 0;

      await db.insert(taskResults).values({
        taskId,
        childId: req.user.childId,
        selectedAnswerId: answerId,
        isCorrect,
        pointsEarned,
      });

      // Mark task as completed
      await db.update(tasks).set({ status: "completed" }).where(eq(tasks.id, taskId));

      if (isCorrect) {
        const child = await db.select().from(children).where(eq(children.id, req.user.childId));
        await db.update(children).set({
          totalPoints: (child[0]?.totalPoints || 0) + pointsEarned,
        }).where(eq(children.id, req.user.childId));

        // Notify parent
        await db.insert(notifications).values({
          parentId: task[0].parentId,
          type: "task",
          message: `ÿ£ÿ¨ÿßÿ® ÿ∑ŸÅŸÑŸÉ ÿπŸÑŸâ ÿßŸÑŸÖŸáŸÖÿ© ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠ Ÿàÿ≠ÿµŸÑ ÿπŸÑŸâ ${pointsEarned} ŸÜŸÇÿ∑ÿ©!`,
          relatedId: taskId,
        });
      }

      res.json({ correct: isCorrect, pointsEarned });
    } catch (error: any) {
      console.error("Error answering task:", error);
      res.status(400).json({ message: error.message });
    }
  });

  // Get Theme Settings
