  app.post("/api/child/buy-product", authMiddleware, async (req: any, res) => {
    try {
      const { orderId, shippingAddress } = req.body;

      if (!orderId) {
        return res.status(400).json({ message: "Order ID is required" });
      }

      const order = await db.select().from(orders).where(eq(orders.id, orderId));
      if (!order[0]) {
        return res.status(400).json({ message: "Product not found" });
      }

      const child = await db.select().from(children).where(eq(children.id, req.user.childId));
      if (!child[0]) {
        return res.status(400).json({ message: "Child not found" });
      }

      if (child[0].totalPoints < order[0].pointsPrice) {
        return res.status(400).json({ message: `ÿπÿØÿØ ÿßŸÑŸÜŸÇÿßÿ∑ ÿ∫Ÿäÿ± ŸÉÿßŸÅŸä. ÿ™ÿ≠ÿ™ÿßÿ¨ ${order[0].pointsPrice - child[0].totalPoints} ŸÜŸÇÿ∑ÿ© ÿ•ÿ∂ÿßŸÅŸäÿ©` });
      }

      // Update child points
      await db.update(children)
        .set({ totalPoints: child[0].totalPoints - order[0].pointsPrice, shippingAddress })
        .where(eq(children.id, req.user.childId));

      // Update order status
      await db.update(orders)
        .set({ status: "completed", shippingAddress })
        .where(eq(orders.id, orderId));

      // Create purchase record
      await db.insert(childPurchases).values({
        childId: req.user.childId,
        productId: order[0].productId,
        orderId,
        pointsSpent: order[0].pointsPrice,
      });

      res.json({ success: true, pointsRemaining: child[0].totalPoints - order[0].pointsPrice });
    } catch (error: any) {
      res.status(500).json({ message: "Purchase failed" });
    }
