  app.post("/api/auth/verify-otp", async (req, res) => {
    try {
      const { email, code } = req.body;
      if (!email || !code) return res.status(400).json({ message: "Email and code required" });

      const otpRecord = await db
        .select()
        .from(otpCodes)
        .where(and(eq(otpCodes.destination, email), eq(otpCodes.code, code), eq(otpCodes.isUsed, false)));

      if (!otpRecord[0]) return res.status(400).json({ message: "Invalid OTP" });
      if (new Date() > otpRecord[0].expiresAt) return res.status(400).json({ message: "OTP expired" });

      await db.update(otpCodes).set({ isUsed: true }).where(eq(otpCodes.id, otpRecord[0].id));

      let parent = await db.select().from(parents).where(eq(parents.email, email));
      if (!parent[0]) {
        const result = await db
          .insert(parents)
          .values({
            email,
            password: "otp-" + Math.random().toString(36).substring(7),
            name: email.split("@")[0],
            uniqueCode: Math.random().toString(36).substring(2, 8).toUpperCase(),
          })
          .returning();
        parent = result;
      }

      const token = jwt.sign({ userId: parent[0].id, type: "parent" }, JWT_SECRET, { expiresIn: "30d" });
      res.json({ token, userId: parent[0].id });
    } catch (error: any) {
      res.status(500).json({ message: "Verification failed" });
    }
  });

