import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { useTheme } from "@/contexts/ThemeContext";
import { saveAuthData, preloadEssentialData } from "@/lib/cache-storage";

export const OTPVerification = (): JSX.Element => {
  const [, navigate] = useLocation();
  const { isDark } = useTheme();
  const [otp, setOtp] = useState("");
  const [email, setEmail] = useState(localStorage.getItem("otpEmail") || "");
  const [time, setTime] = useState(300);

  useEffect(() => {
    const timer = setInterval(() => setTime(t => t - 1), 1000);
    if (time <= 0) navigate("/parent-auth");
    return () => clearInterval(timer);
  }, [time, navigate]);

  const verifyOtpMutation = useMutation({
    mutationFn: async () => {
      const res = await fetch("/api/auth/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code: otp }),
      });
      if (!res.ok) throw new Error("Invalid OTP");
      return res.json();
    },
    onSuccess: async (data: any) => {
      await saveAuthData(data.token, "parent", data.userId);
      localStorage.removeItem("otpEmail");
      preloadEssentialData("parent", data.token);
      navigate("/parent-dashboard");
    },
    onError: () => alert("‚ùå ÿ±ŸÖÿ≤ ÿÆÿßÿ∑ÿ¶ ÿ£Ÿà ŸÖŸÜÿ™ŸáŸä ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿ©"),
  });

  return (
    <div className={`min-h-screen flex items-center justify-center ${isDark ? "bg-gray-900" : "bg-gray-100"}`}>
      <div className={`${isDark ? "bg-gray-800" : "bg-white"} rounded-lg shadow-lg p-8 max-w-md w-full`}>
        <h1 className={`text-3xl font-bold text-center mb-2 ${isDark ? "text-white" : "text-gray-800"}`}>
          üîê ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸáŸàŸäÿ©
        </h1>
        <p className={`text-center mb-6 ${isDark ? "text-gray-400" : "text-gray-600"}`}>
          ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ŸÖÿ≤ ÿ™ÿ≠ŸÇŸÇ ÿ•ŸÑŸâ {email}
        </p>

        <div className="mb-6">
          <label className={`block font-bold mb-2 ${isDark ? "text-white" : "text-gray-800"}`}>
            ÿ£ÿØÿÆŸÑ ÿßŸÑÿ±ŸÖÿ≤ (6 ÿ£ÿ±ŸÇÿßŸÖ)
          </label>
          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value.replace(/\D/g, "").slice(0, 6))}
            maxLength={6}
            className={`w-full px-4 py-2 text-center text-2xl tracking-widest border-2 rounded-lg focus:outline-none focus:border-blue-500 ${
              isDark ? "bg-gray-700 border-gray-600 text-white" : "border-gray-300"
            }`}
            placeholder="000000"
          />
        </div>

        <div className="text-center mb-6">
          <p className={`text-sm ${isDark ? "text-gray-400" : "text-gray-600"}`}>
            ‚è±Ô∏è ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä: {Math.floor(time / 60)}:{(time % 60).toString().padStart(2, "0")}
          </p>
        </div>

        <button
          onClick={() => verifyOtpMutation.mutate()}
          disabled={verifyOtpMutation.isPending || otp.length !== 6}
          className="w-full px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white font-bold rounded-lg disabled:opacity-50 mb-2"
        >
          ‚úÖ ÿßŸÑÿ™ÿ≠ŸÇŸÇ
        </button>

        <button
          onClick={() => navigate("/parent-auth")}
          className={`w-full px-4 py-2 border-2 rounded-lg font-bold ${
            isDark ? "border-gray-600 text-gray-300 hover:bg-gray-700" : "border-gray-300 text-gray-700 hover:bg-gray-100"
          }`}
        >
          ‚Üê ÿ•ŸÑÿ∫ÿßÿ°
        </button>
      </div>
    </div>
  );
};

