import { useState } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { useTheme } from "@/contexts/ThemeContext";
import { saveAuthData } from "@/lib/cache-storage";

export const AdminAuth = (): JSX.Element => {
  const [, navigate] = useLocation();
  const { isDark } = useTheme();
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const loginMutation = useMutation({
    mutationFn: async () => {
      const res = await fetch("/api/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      if (!res.ok) throw new Error("Login failed");
      return res.json();
    },
    onSuccess: async (data) => {
      await saveAuthData(data.token, "admin", data.adminId || "admin");
      navigate("/admin-dashboard");
    },
    onError: () => alert("ÿ®ŸäÿßŸÜÿßÿ™ ÿÆÿßÿ∑ÿ¶ÿ©"),
  });

  const registerMutation = useMutation({
    mutationFn: async () => {
      if (password !== confirmPassword) throw new Error("Passwords don't match");
      const res = await fetch("/api/admin/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      if (!res.ok) throw new Error("Registration failed");
      return res.json();
    },
    onSuccess: async (data) => {
      await saveAuthData(data.token, "admin", data.adminId || "admin");
      navigate("/admin-dashboard");
    },
    onError: () => alert("‚ùå ŸÅÿ¥ŸÑ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ"),
  });

  return (
    <div className={`min-h-screen flex items-center justify-center ${isDark ? "bg-gray-900" : "bg-gray-100"}`}>
      <div className={`${isDark ? "bg-gray-800" : "bg-white"} rounded-lg shadow-lg p-8 max-w-md w-full`}>
        <h1 className={`text-3xl font-bold text-center mb-8 ${isDark ? "text-white" : "text-gray-800"}`}>
          üîê Admin Panel
        </h1>

        <div className="space-y-6">
          <div className={`p-4 rounded-lg ${isDark ? "bg-gray-700" : "bg-gray-100"}`}>
            <p className={`text-sm ${isDark ? "text-gray-300" : "text-gray-600"}`}>
              {isLogin ? "ÿ£ÿØÿÆŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ≠ÿ≥ÿßÿ®ŸÉ" : "ÿ£ŸÜÿ¥ÿ¶ ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ"}
            </p>
          </div>

          <div>
            <label className={`block font-bold mb-2 ${isDark ? "text-white" : "text-gray-800"}`}>
              ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
            </label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={`w-full px-4 py-2 border-2 rounded-lg focus:outline-none focus:border-blue-500 ${
                isDark ? "bg-gray-700 border-gray-600 text-white" : "border-gray-300"
              }`}
              placeholder="admin@example.com"
            />
          </div>

          <div>
            <label className={`block font-bold mb-2 ${isDark ? "text-white" : "text-gray-800"}`}>
              ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className={`w-full px-4 py-2 border-2 rounded-lg focus:outline-none focus:border-blue-500 ${
                isDark ? "bg-gray-700 border-gray-600 text-white" : "border-gray-300"
              }`}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
            />
          </div>

          {!isLogin && (
            <div>
              <label className={`block font-bold mb-2 ${isDark ? "text-white" : "text-gray-800"}`}>
                ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
              </label>
              <input
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                className={`w-full px-4 py-2 border-2 rounded-lg focus:outline-none focus:border-blue-500 ${
                  isDark ? "bg-gray-700 border-gray-600 text-white" : "border-gray-300"
                }`}
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              />
            </div>
          )}

          <button
            onClick={() => (isLogin ? loginMutation.mutate() : registerMutation.mutate())}
            disabled={loginMutation.isPending || registerMutation.isPending}
            className="w-full px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white font-bold rounded-lg disabled:opacity-50"
          >
            {isLogin ? "üîë ÿØÿÆŸàŸÑ" : "‚úÖ ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ®"}
          </button>

          <button
            onClick={() => setIsLogin(!isLogin)}
            className={`w-full px-4 py-2 border-2 rounded-lg font-bold ${
              isDark ? "border-gray-600 text-gray-300 hover:bg-gray-700" : "border-gray-300 text-gray-700 hover:bg-gray-100"
            }`}
          >
            {isLogin ? "üìù ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü" : "üîë ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ® ÿ®ÿßŸÑŸÅÿπŸÑÿü"}
          </button>

          <button
            onClick={() => navigate("/")}
            className={`w-full px-4 py-2 text-center font-bold ${isDark ? "text-gray-400 hover:text-gray-300" : "text-gray-600 hover:text-gray-800"}`}
          >
            ‚Üê ÿßŸÑÿ±ÿ¨Ÿàÿπ ŸÑŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
          </button>
        </div>
      </div>
    </div>
  );
};

