import React, { useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useTheme } from "@/contexts/ThemeContext";

export const Notifications = (): JSX.Element => {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const { isDark, toggleTheme } = useTheme();
  const token = localStorage.getItem("token");

  const { data: notifications, refetch } = useQuery({
    queryKey: ["notifications"],
    queryFn: async () => {
      const res = await fetch("/api/parent/notifications", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    enabled: !!token,
    refetchInterval: 5000,
  });

  const markReadMutation = useMutation({
    mutationFn: async (notificationId: string) => {
      const res = await fetch(`/api/parent/notifications/${notificationId}/read`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    onSuccess: () => {
      refetch();
    },
  });

  useEffect(() => {
    const interval = setInterval(() => refetch(), 5000);
    return () => clearInterval(interval);
  }, [refetch]);

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "deposit":
      case "deposit_approved":
      case "deposit_rejected":
        return "üí≥";
      case "purchase":
        return "üõçÔ∏è";
      case "task":
        return "üìù";
      case "points":
        return "‚≠ê";
      case "order":
        return "üì¶";
      default:
        return "üîî";
    }
  };

  const handleNotificationClick = (notification: any) => {
    markReadMutation.mutate(notification.id);
    switch (notification.type) {
      case "deposit":
      case "deposit_approved":
      case "deposit_rejected":
        navigate("/wallet");
        break;
      case "purchase":
      case "order":
        navigate("/parent-store");
        break;
      case "task":
        navigate("/create-task");
        break;
      case "points":
        navigate("/parent-dashboard");
        break;
      default:
        break;
    }
  };

  const unreadCount = notifications?.filter((n: any) => !n.isRead).length || 0;

  return (
    <div className={`min-h-screen p-6 ${isDark ? "bg-gray-900" : "bg-gray-50"}`}>
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className={`text-4xl font-bold ${isDark ? "text-white" : "text-gray-800"}`}>
              üîî ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™
            </h1>
            {unreadCount > 0 && (
              <p className={isDark ? "text-gray-400" : "text-gray-600"}>
                ŸÑÿØŸäŸÉ {unreadCount} ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿ¨ÿØŸäÿØÿ©
              </p>
            )}
          </div>
          <div className="flex gap-2">
            <button
              onClick={toggleTheme}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg font-bold"
            >
              {isDark ? "‚òÄÔ∏è" : "üåô"}
            </button>
            <button
              onClick={() => navigate("/parent-dashboard")}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg font-bold"
            >
              ‚Üê ÿ±ÿ¨Ÿàÿπ
            </button>
          </div>
        </div>

        {/* Notifications List */}
        <div className="space-y-4">
          {notifications && notifications.length > 0 ? (
            notifications.map((notification: any) => (
              <div
                key={notification.id}
                onClick={() => handleNotificationClick(notification)}
                className={`${
                  isDark ? "bg-gray-800 hover:bg-gray-700" : "bg-white hover:bg-gray-50"
                } rounded-lg p-4 shadow flex justify-between items-start gap-4 transition-all cursor-pointer ${
                  !notification.isRead ? (isDark ? "border-l-4 border-blue-500" : "border-l-4 border-blue-500") : ""
                }`}
                data-testid={`notification-item-${notification.id}`}
              >
                <div className="flex gap-4 flex-1">
                  <span className="text-2xl">{getNotificationIcon(notification.type)}</span>
                  <div className="flex-1">
                    <p
                      className={`font-bold ${
                        isDark ? "text-white" : "text-gray-800"
                      } ${!notification.isRead ? "font-bold" : ""}`}
                    >
                      {notification.message}
                    </p>
                    <p className={`text-sm ${isDark ? "text-gray-400" : "text-gray-600"}`}>
                      {new Date(notification.createdAt).toLocaleDateString("ar-EG", {
                        year: "numeric",
                        month: "long",
                        day: "numeric",
                        hour: "2-digit",
                        minute: "2-digit",
                      })}
                    </p>
                    <p className={`text-xs ${isDark ? "text-gray-500" : "text-gray-400"} mt-1`}>
                      ÿßÿ∂ÿ∫ÿ∑ ŸÑŸÑÿßŸÜÿ™ŸÇÿßŸÑ
                    </p>
                  </div>
                </div>
                {!notification.isRead && (
                  <span className="px-2 py-1 text-xs bg-blue-500 text-white rounded-full">
                    ÿ¨ÿØŸäÿØ
                  </span>
                )}
              </div>
            ))
          ) : (
            <div className={`${isDark ? "bg-gray-800" : "bg-white"} rounded-lg p-8 text-center shadow`}>
              <p className={`text-lg ${isDark ? "text-gray-400" : "text-gray-600"}`}>
                ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿ≠ÿßŸÑŸäÿßŸã ‚ú®
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

