    queryKey: ["templates", selectedSubject?.id],
    queryFn: async () => {
      if (!selectedSubject) return [];
      const res = await fetch(`/api/subjects/${selectedSubject.id}/templates`);
      return res.json();
    },
    enabled: !!selectedSubject,
  });

  const { data: children } = useQuery({
    queryKey: ["parent-children"],
    queryFn: async () => {
      const res = await fetch("/api/parent/children", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    enabled: !!token,
  });

  const sendTaskMutation = useMutation({
    mutationFn: async ({ templateId, childId }: any) => {
      const res = await fetch("/api/parent/send-template-task", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ templateId, childId }),
      });
      if (!res.ok) throw new Error("Failed to send task");
      return res.json();
    },
    onSuccess: () => {
      alert("‚úÖ ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÖŸáŸÖÿ© ÿ®ŸÜÿ¨ÿßÿ≠!");
      setSelectedTemplate(null);
