import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useTheme } from "@/contexts/ThemeContext";
import { queryClient } from "@/lib/queryClient";

export const AssignTask = (): JSX.Element => {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const { isDark, toggleTheme } = useTheme();
  const token = localStorage.getItem("token");

  const [selectedChild, setSelectedChild] = useState("");
  const [selectedSubject, setSelectedSubject] = useState("");
  const [selectedTemplate, setSelectedTemplate] = useState("");
  const [createCustom, setCreateCustom] = useState(false);
  const [customQuestion, setCustomQuestion] = useState("");
  const [customPoints, setCustomPoints] = useState(10);
  const [customAnswers, setCustomAnswers] = useState([
    { id: "1", text: "", isCorrect: true },
    { id: "2", text: "", isCorrect: false },
    { id: "3", text: "", isCorrect: false },
    { id: "4", text: "", isCorrect: false },
  ]);

  const { data: children } = useQuery({
    queryKey: ["parent-children"],
    queryFn: async () => {
      const res = await fetch("/api/parent/children", {
        headers: { Authorization: `Bearer ${token}` },
      });
      return res.json();
    },
    enabled: !!token,
  });

  const { data: subjects } = useQuery({
    queryKey: ["subjects"],
    queryFn: async () => {
      const res = await fetch("/api/subjects");
      return res.json();
    },
  });

  const { data: templates } = useQuery({
    queryKey: ["template-tasks", selectedSubject],
    queryFn: async () => {
      const res = await fetch(`/api/template-tasks?subjectId=${selectedSubject}`);
      return res.json();
    },
    enabled: !!selectedSubject,
  });

  const assignMutation = useMutation({
    mutationFn: async () => {
      let taskData;

      if (createCustom) {
        taskData = {
          childId: selectedChild,
          question: customQuestion,
          pointsReward: customPoints,
          answers: customAnswers.filter(a => a.text.trim()),
        };
      } else {
        const template = templates?.find((t: any) => t.id === selectedTemplate);
        if (!template) throw new Error("Template not found");
        taskData = {
          childId: selectedChild,
          question: template.question,
          pointsReward: template.pointsReward,
          answers: template.answers,
          templateId: template.id,
        };
      }

      const res = await fetch("/api/parent/assign-task", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(taskData),
      });

      if (!res.ok) throw new Error("Failed to assign task");
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["parent-children"] });
      navigate("/parent-dashboard");
    },
  });

  const selectedTemplateData = templates?.find((t: any) => t.id === selectedTemplate);

  return (
    <div className={`min-h-screen ${isDark ? "bg-gray-900" : "bg-gradient-to-b from-blue-50 to-purple-50"}`}>
      <div className="max-w-4xl mx-auto p-6">
        <div className="flex justify-between items-center mb-8">
          <h1 className={`text-3xl font-bold ${isDark ? "text-white" : "text-gray-800"}`}>
            üìù ÿ•ÿ±ÿ≥ÿßŸÑ ŸÖŸáŸÖÿ© ŸÑŸÑÿ∑ŸÅŸÑ
          </h1>
          <div className="flex gap-2">
            <button
              onClick={toggleTheme}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg font-bold"
              data-testid="button-theme-toggle"
            >
              {isDark ? "‚òÄÔ∏è" : "üåô"}
            </button>
            <button
              onClick={() => navigate("/parent-dashboard")}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg font-bold"
              data-testid="button-back"
            >
              ‚Üê ÿ±ÿ¨Ÿàÿπ
            </button>
          </div>
        </div>

        <div className={`${isDark ? "bg-gray-800" : "bg-white"} rounded-2xl shadow-lg p-8`}>
          <div className="space-y-6">
            <div>
              <label className={`block text-lg font-bold mb-3 ${isDark ? "text-white" : "text-gray-800"}`}>
                üë∂ ÿßÿÆÿ™ÿ± ÿßŸÑÿ∑ŸÅŸÑ
              </label>
              <select
                value={selectedChild}
                onChange={(e) => setSelectedChild(e.target.value)}
                className={`w-full px-4 py-3 rounded-xl border-2 text-lg ${
                  isDark ? "bg-gray-700 border-gray-600 text-white" : "bg-white border-gray-300"
                }`}
                data-testid="select-child"
              >
                <option value="">-- ÿßÿÆÿ™ÿ± ÿ∑ŸÅŸÑ --</option>
