import { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Bell, Smartphone, Shield, CheckCircle, XCircle, ChevronRight, Download } from "lucide-react";

interface ChildPermissionsSetupProps {
  onComplete: () => void;
}

export function ChildPermissionsSetup({ onComplete }: ChildPermissionsSetupProps) {
  const { t } = useTranslation();
  const [notificationPermission, setNotificationPermission] = useState<NotificationPermission>("default");
  const [isPWAInstalled, setIsPWAInstalled] = useState(false);
  const [deferredPrompt, setDeferredPrompt] = useState<any>(null);
  const [currentStep, setCurrentStep] = useState(0);

  useEffect(() => {
    if ("Notification" in window) {
      setNotificationPermission(Notification.permission);
    }

    const isStandalone = window.matchMedia("(display-mode: standalone)").matches;
    setIsPWAInstalled(isStandalone);

    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      setDeferredPrompt(e);
    };

    window.addEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
    return () => window.removeEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
  }, []);

  const requestNotificationPermission = async () => {
    if ("Notification" in window) {
      const permission = await Notification.requestPermission();
      setNotificationPermission(permission);
      if (permission === "granted") {
        setCurrentStep(1);
      }
    }
  };

  const installPWA = async () => {
    if (deferredPrompt) {
      deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;
      if (outcome === "accepted") {
        setIsPWAInstalled(true);
      }
      setDeferredPrompt(null);
    }
  };

  const handleComplete = () => {
    localStorage.setItem("child_permissions_setup_complete", "true");
    onComplete();
  };

  const steps = [
    {
      icon: Bell,
      title: t("permissions.notifications", "ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™"),
      description: t("permissions.notificationsDesc", "ŸÑÿ™ŸÑŸÇŸä ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿßŸÑŸÖŸáÿßŸÖ ŸàÿßŸÑÿ™ŸÜÿ®ŸäŸáÿßÿ™ ŸÖŸÜ ÿßŸÑŸàÿßŸÑÿØŸäŸÜ"),
      action: requestNotificationPermission,
      actionLabel: t("permissions.allow", "ÿßŸÑÿ≥ŸÖÿßÿ≠"),
      status: notificationPermission === "granted" ? "granted" : notificationPermission === "denied" ? "denied" : "pending",
    },
    {
      icon: Download,
      title: t("permissions.install", "ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ"),
      description: t("permissions.installDesc", "ÿ£ÿ∂ŸÅ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ŸÑŸÑÿ¥ÿßÿ¥ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÑŸÑŸàÿµŸàŸÑ ÿßŸÑÿ≥ÿ±Ÿäÿπ"),
      action: installPWA,
      actionLabel: t("permissions.installApp", "ÿ™ÿ´ÿ®Ÿäÿ™"),
      status: isPWAInstalled ? "granted" : deferredPrompt ? "pending" : "unavailable",
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-600 to-indigo-700 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="mx-auto w-16 h-16 bg-purple-100 dark:bg-purple-900 rounded-full flex items-center justify-center mb-4">
            <Shield className="w-8 h-8 text-purple-600 dark:text-purple-400" />
          </div>
          <CardTitle className="text-2xl">{t("permissions.title", "ÿ•ÿπÿØÿßÿØ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ")}</CardTitle>
          <CardDescription>
            {t("permissions.subtitle", "ŸÜÿ≠ÿ™ÿßÿ¨ ÿ®ÿπÿ∂ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™ ŸÑÿ™ÿπŸÖŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸÖŸäÿ≤ÿßÿ™ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠")}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {steps.map((step, index) => {
            const Icon = step.icon;
            const isGranted = step.status === "granted";
            const isDenied = step.status === "denied";
            const isUnavailable = step.status === "unavailable";

            return (
              <div
                key={index}
                className={`flex items-center gap-4 p-4 rounded-lg border ${
