import { useState, useEffect, useCallback } from "react";
import { useTranslation } from "react-i18next";
import { Button } from "@/components/ui/button";
import { Download, ExternalLink, RefreshCw } from "lucide-react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";

interface BeforeInstallPromptEvent extends Event {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
}

interface PWAInstallButtonProps {
  variant?: "default" | "outline" | "ghost" | "secondary";
  size?: "default" | "sm" | "lg" | "icon";
  showText?: boolean;
  className?: string;
}

let deferredPrompt: BeforeInstallPromptEvent | null = null;

export function PWAInstallButton({ 
  variant = "default", 
  size = "default",
  showText = true,
  className = ""
}: PWAInstallButtonProps) {
  const { i18n } = useTranslation();
  const { toast } = useToast();
  const isArabic = i18n.language === "ar";
  const [isRunningStandalone, setIsRunningStandalone] = useState(false);
  const [wasInstalled, setWasInstalled] = useState(false);
  const [canInstall, setCanInstall] = useState(false);
  const [showReinstallDialog, setShowReinstallDialog] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const standaloneCheck = window.matchMedia("(display-mode: standalone)").matches 
      || (window.navigator as any).standalone === true;
    setIsRunningStandalone(standaloneCheck);

    const installedFlag = localStorage.getItem("pwa_installed") === "true";
    setWasInstalled(installedFlag);

    if (deferredPrompt) {
      setCanInstall(true);
    }

    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      deferredPrompt = e as BeforeInstallPromptEvent;
      setCanInstall(true);
    };

    const handleAppInstalled = () => {
      localStorage.setItem("pwa_installed", "true");
      setWasInstalled(true);
      setCanInstall(false);
      deferredPrompt = null;
      toast({
        title: isArabic ? "ÿ™ŸÖ ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™ ÿ®ŸÜÿ¨ÿßÿ≠" : "Installed Successfully",
        description: isArabic ? "ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ•ŸÑŸâ ÿ¥ÿßÿ¥ÿ™ŸÉ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©" : "App added to your home screen",
      });
    };

    window.addEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
    window.addEventListener("appinstalled", handleAppInstalled);

    return () => {
      window.removeEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
      window.removeEventListener("appinstalled", handleAppInstalled);
    };
  }, [isArabic, toast]);

  const triggerInstall = useCallback(async () => {
    if (!deferredPrompt) return false;

    try {
      await deferredPrompt.prompt();
      const result = await deferredPrompt.userChoice;

      if (result.outcome === "accepted") {
        localStorage.setItem("pwa_installed", "true");
        setWasInstalled(true);
        deferredPrompt = null;
        setCanInstall(false);
        return true;
      } else {
        deferredPrompt = null;
        setCanInstall(false);
        return false;
      }
    } catch {
      return false;
    }
  }, []);

  const handleInstallClick = useCallback(async () => {
    setIsLoading(true);

    try {
      if (isRunningStandalone) {
        window.location.href = "/";
        return;
      }

      if (wasInstalled && canInstall) {
        setShowReinstallDialog(true);
        return;
      }

      if (wasInstalled && !canInstall) {
        window.open("/", "_blank");
        return;
      }

      if (canInstall && deferredPrompt) {
        const installed = await triggerInstall();
        if (installed) {
          setTimeout(() => {
            window.location.href = "/";
          }, 500);
        }
        return;
      }

      if (!canInstall && !wasInstalled) {
        toast({
          title: isArabic ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ÿ∂Ÿäÿ±..." : "Preparing...",
          description: isArabic ? "ÿßŸÜÿ™ÿ∏ÿ± ŸÇŸÑŸäŸÑÿßŸã ÿ´ŸÖ ÿ≠ÿßŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ" : "Please wait and try again",
        });
      }

    } finally {
      setIsLoading(false);
    }
  }, [isRunningStandalone, wasInstalled, canInstall, triggerInstall, isArabic, toast]);

  const handleReinstallConfirm = useCallback(async () => {
    setShowReinstallDialog(false);
    setIsLoading(true);

    try {
      const installed = await triggerInstall();
      if (installed) {
        setTimeout(() => {
          window.location.href = "/";
        }, 500);
      }
    } finally {
      setIsLoading(false);
    }
  }, [triggerInstall]);

  const getButtonText = () => {
    if (isLoading) {
      return isArabic ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..." : "Loading...";
    }
    if (isRunningStandalone) {
      return isArabic ? "ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©" : "Home";
    }
    if (wasInstalled && canInstall) {
      return isArabic ? "ÿ•ÿπÿßÿØÿ© ÿ™ÿ´ÿ®Ÿäÿ™" : "Reinstall";
    }
    if (wasInstalled) {
      return isArabic ? "ŸÅÿ™ÿ≠ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Open App";
    }
    return isArabic ? "ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Install App";
  };

  const getButtonIcon = () => {
    if (isLoading) {
      return <RefreshCw className="h-4 w-4 animate-spin" />;
    }
    if (isRunningStandalone || wasInstalled) {
      return <ExternalLink className="h-4 w-4" />;
    }
    return <Download className="h-4 w-4" />;
  };

  return (
    <>
      <Button
        variant={variant}
        size={size}
        onClick={handleInstallClick}
        className={className}
        disabled={isLoading}
        data-testid="button-pwa-install"
      >
        {getButtonIcon()}
        {showText && <span className="mr-2">{getButtonText()}</span>}
      </Button>

      <AlertDialog open={showReinstallDialog} onOpenChange={setShowReinstallDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>
              {isArabic ? "ÿ•ÿπÿßÿØÿ© ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Reinstall App"}
            </AlertDialogTitle>
            <AlertDialogDescription>
              {isArabic 
                ? "Ÿäÿ®ÿØŸà ÿ£ŸÜ ÿ£ŸäŸÇŸàŸÜÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©. ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ÿπÿßÿØÿ© ÿ•ÿ∂ÿßŸÅÿ™Ÿáÿß ÿ•ŸÑŸâ ÿ¥ÿßÿ¥ÿ™ŸÉ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©ÿü" 
                : "The app icon seems to be missing. Would you like to add it back to your home screen?"}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-reinstall">
              {isArabic ? "ÿ•ŸÑÿ∫ÿßÿ°" : "Cancel"}
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleReinstallConfirm}
              data-testid="button-confirm-reinstall"
            >
              {isArabic ? "ŸÜÿπŸÖÿå ÿ£ÿπÿØ ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™" : "Yes, Reinstall"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

