/**
 * Cached API Request Utility
 * Implements Cache Priority + Stale-While-Revalidate pattern
 * Returns cached data instantly while updating in background
 */

import { 
  getCachedApiResponse, 
  setCachedApiResponse, 
  getAuthData 
} from './cache-storage';

interface CachedFetchOptions extends RequestInit {
  skipCache?: boolean;
  forceRefresh?: boolean;
  cacheKey?: string;
}

interface CachedResponse<T> {
  data: T;
  fromCache: boolean;
  isStale: boolean;
}

/**
 * Fetch with cache-first strategy
 * Returns cached data immediately, updates in background
 */
export async function cachedFetch<T>(
  url: string, 
  options: CachedFetchOptions = {}
): Promise<CachedResponse<T>> {
  const { skipCache = false, forceRefresh = false, cacheKey, ...fetchOptions } = options;
  const effectiveCacheKey = cacheKey || url;

  // Get auth token for authorized requests
  const authData = await getAuthData();
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
    ...fetchOptions.headers,
  };

  if (authData?.token && !url.includes('/api/auth/')) {
    (headers as Record<string, string>)['Authorization'] = `Bearer ${authData.token}`;
  }

  // Try to get cached data first (unless skipping cache or forcing refresh)
  if (!skipCache && !forceRefresh) {
    const cachedData = await getCachedApiResponse<T>(effectiveCacheKey);

    if (cachedData !== null) {
      // Return cached data immediately
      // Update cache in background
      fetchAndUpdateCache<T>(url, { ...fetchOptions, headers }, effectiveCacheKey);

      return {
        data: cachedData,
        fromCache: true,
        isStale: false
      };
    }
  }

  // No cache available, fetch from network
  try {
    const response = await fetch(url, { ...fetchOptions, headers });

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }

    const data = await response.json() as T;

    // Cache the response
    if (!skipCache) {
      await setCachedApiResponse(effectiveCacheKey, data);
    }

    return {
      data,
      fromCache: false,
      isStale: false
    };
  } catch (error) {
    // If network fails, try to return cached data as fallback
    const cachedData = await getCachedApiResponse<T>(effectiveCacheKey);

    if (cachedData !== null) {
      return {
        data: cachedData,
        fromCache: true,
        isStale: true
      };
    }

    throw error;
  }
}

/**
 * Fetch and update cache in background
 * Used for stale-while-revalidate pattern
 */
async function fetchAndUpdateCache<T>(
  url: string, 
  options: RequestInit,
  cacheKey: string
): Promise<void> {
  try {
    const response = await fetch(url, options);

    if (response.ok) {
      const data = await response.json() as T;
      await setCachedApiResponse(cacheKey, data);
    }
  } catch (error) {
    console.log('[CachedAPI] Background update failed:', error);
  }
}

/**
 * Pre-fetch and cache multiple URLs
 * Used for smart preloading after login
 */
export async function prefetchUrls(urls: string[]): Promise<void> {
  const authData = await getAuthData();
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
  };

  if (authData?.token) {
    (headers as Record<string, string>)['Authorization'] = `Bearer ${authData.token}`;
  }

  await Promise.allSettled(
    urls.map(async (url) => {
      try {
        const response = await fetch(url, { headers });
        if (response.ok) {
          const data = await response.json();
          await setCachedApiResponse(url, data);
        }
      } catch (error) {
        console.log(`[CachedAPI] Prefetch failed for ${url}:`, error);
      }
    })
  );
}

/**
 * Create a cached query function for TanStack Query
 * This wraps the cache-first logic for use with useQuery
 */
export function createCachedQueryFn<T>(url: string) {
  return async (): Promise<T> => {
    const result = await cachedFetch<T>(url);
    return result.data;
  };
}

/**
 * Get cached data synchronously (for initial render)
 * This is a simplified version that uses localStorage fallback
 */
export function getCachedDataSync<T>(key: string): T | null {
  try {
    const cached = localStorage.getItem(`cache:${key}`);
    if (cached) {
      const parsed = JSON.parse(cached);
      if (parsed.expiresAt > Date.now()) {
        return parsed.data as T;
      }
    }
  } catch (error) {
    console.log('[CachedAPI] Sync cache read error:', error);
  }
  return null;
}

/**
 * Set cached data synchronously (for quick storage)
 */
export function setCachedDataSync<T>(key: string, data: T, ttlMs: number = 5 * 60 * 1000): void {
  try {
    localStorage.setItem(`cache:${key}`, JSON.stringify({
      data,
      timestamp: Date.now(),
      expiresAt: Date.now() + ttlMs
    }));
  } catch (error) {
    console.log('[CachedAPI] Sync cache write error:', error);
  }
}

