/**
 * Cache Storage Utility
 * Provides IndexedDB-based caching for API responses and user data
 * Uses Cache Priority + Stale-While-Revalidate pattern
 */

const DB_NAME = 'classify-cache';
const DB_VERSION = 1;
const STORES = {
  AUTH: 'auth',
  API_CACHE: 'api-cache',
  USER_DATA: 'user-data',
  SETTINGS: 'settings'
};

interface CacheEntry<T = unknown> {
  key: string;
  data: T;
  timestamp: number;
  expiresAt: number;
}

interface AuthData {
  token: string;
  userType: 'parent' | 'child' | 'admin';
  userId: string;
  expiresAt: number;
}

let db: IDBDatabase | null = null;

function openDB(): Promise<IDBDatabase> {
  return new Promise((resolve, reject) => {
    if (db) {
      resolve(db);
      return;
    }

    const request = indexedDB.open(DB_NAME, DB_VERSION);

    request.onerror = () => reject(request.error);

    request.onsuccess = () => {
      db = request.result;
      resolve(db);
    };

    request.onupgradeneeded = (event) => {
      const database = (event.target as IDBOpenDBRequest).result;

      if (!database.objectStoreNames.contains(STORES.AUTH)) {
        database.createObjectStore(STORES.AUTH, { keyPath: 'key' });
      }

      if (!database.objectStoreNames.contains(STORES.API_CACHE)) {
        const apiStore = database.createObjectStore(STORES.API_CACHE, { keyPath: 'key' });
        apiStore.createIndex('expiresAt', 'expiresAt', { unique: false });
      }

      if (!database.objectStoreNames.contains(STORES.USER_DATA)) {
        database.createObjectStore(STORES.USER_DATA, { keyPath: 'key' });
      }

      if (!database.objectStoreNames.contains(STORES.SETTINGS)) {
        database.createObjectStore(STORES.SETTINGS, { keyPath: 'key' });
      }
    };
  });
}

async function getFromStore<T>(storeName: string, key: string): Promise<T | null> {
  try {
    const database = await openDB();
    return new Promise((resolve, reject) => {
      const transaction = database.transaction(storeName, 'readonly');
      const store = transaction.objectStore(storeName);
      const request = store.get(key);

      request.onsuccess = () => {
        const entry = request.result as CacheEntry<T> | undefined;
        if (entry && entry.expiresAt > Date.now()) {
          resolve(entry.data);
        } else {
          resolve(null);
        }
      };

      request.onerror = () => reject(request.error);
    });
  } catch (error) {
    console.error('IndexedDB get error:', error);
    return null;
  }
}

async function setInStore<T>(
  storeName: string, 
  key: string, 
  data: T, 
  ttlMs: number = 24 * 60 * 60 * 1000
): Promise<void> {
  try {
    const database = await openDB();
    return new Promise((resolve, reject) => {
      const transaction = database.transaction(storeName, 'readwrite');
      const store = transaction.objectStore(storeName);

      const entry: CacheEntry<T> = {
        key,
        data,
        timestamp: Date.now(),
        expiresAt: Date.now() + ttlMs
      };

      const request = store.put(entry);
      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  } catch (error) {
    console.error('IndexedDB set error:', error);
  }
}

async function deleteFromStore(storeName: string, key: string): Promise<void> {
  try {
    const database = await openDB();
    return new Promise((resolve, reject) => {
      const transaction = database.transaction(storeName, 'readwrite');
      const store = transaction.objectStore(storeName);
      const request = store.delete(key);

      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  } catch (error) {
    console.error('IndexedDB delete error:', error);
  }
}

async function clearStore(storeName: string): Promise<void> {
  try {
    const database = await openDB();
    return new Promise((resolve, reject) => {
      const transaction = database.transaction(storeName, 'readwrite');
      const store = transaction.objectStore(storeName);
      const request = store.clear();

      request.onsuccess = () => resolve();
      request.onerror = () => reject(request.error);
    });
  } catch (error) {
    console.error('IndexedDB clear error:', error);
  }
}

// Auth-specific functions
export async function saveAuthData(token: string, userType: 'parent' | 'child' | 'admin', userId: string): Promise<void> {
  const expiresAt = Date.now() + (30 * 24 * 60 * 60 * 1000); // 30 days (matches JWT expiry)

  const authData: AuthData = {
    token,
    userType,
    userId,
    expiresAt
  };

  // Save to IndexedDB for persistence
  await setInStore(STORES.AUTH, 'session', authData, 30 * 24 * 60 * 60 * 1000);

  // Also save to localStorage for quick synchronous access
  localStorage.setItem('token', token);
  localStorage.setItem('userType', userType);
  localStorage.setItem('userId', userId);
  localStorage.setItem('tokenExpiresAt', String(expiresAt));
}

// Synchronous auth check for immediate use
export function getAuthDataSync(): AuthData | null {
  const token = localStorage.getItem('token');
  const userType = localStorage.getItem('userType') as 'parent' | 'child' | 'admin' | null;
  const userId = localStorage.getItem('userId');
  const expiresAtStr = localStorage.getItem('tokenExpiresAt');

  if (!token || !userType || !userId) return null;

  const expiresAt = expiresAtStr ? parseInt(expiresAtStr, 10) : Date.now() + (24 * 60 * 60 * 1000);

  // Check if token is expired
  if (expiresAt < Date.now()) {
    clearAuthDataSync();
    return null;
  }

  return { token, userType, userId, expiresAt };
}

// Synchronous clear for logout
export function clearAuthDataSync(): void {
  localStorage.removeItem('token');
  localStorage.removeItem('userType');
  localStorage.removeItem('userId');
  localStorage.removeItem('childId');
  localStorage.removeItem('parentId');
  localStorage.removeItem('tokenExpiresAt');
}

export async function getAuthData(): Promise<AuthData | null> {
  // Try synchronous localStorage first for speed
  const syncAuth = getAuthDataSync();
  if (syncAuth) {
    return syncAuth;
  }

  // Try IndexedDB as backup
  const authData = await getFromStore<AuthData>(STORES.AUTH, 'session');
  if (authData && authData.expiresAt > Date.now()) {
    // Sync back to localStorage
    localStorage.setItem('token', authData.token);
    localStorage.setItem('userType', authData.userType);
    localStorage.setItem('userId', authData.userId);
    localStorage.setItem('tokenExpiresAt', String(authData.expiresAt));
    return authData;
  }

  return null;
}

export async function clearAuthData(): Promise<void> {
  clearAuthDataSync();
  await deleteFromStore(STORES.AUTH, 'session');
  await clearStore(STORES.API_CACHE);
  await clearStore(STORES.USER_DATA);
}

// API Cache functions with stale-while-revalidate
const API_CACHE_TTL: Record<string, number> = {
  '/api/subjects': 24 * 60 * 60 * 1000, // 24 hours
  '/api/seo-settings': 24 * 60 * 60 * 1000,
  '/api/theme-settings': 12 * 60 * 60 * 1000,
  '/api/parent/me': 5 * 60 * 1000, // 5 minutes
  '/api/child/me': 5 * 60 * 1000,
  '/api/children': 10 * 60 * 1000, // 10 minutes
  '/api/tasks': 5 * 60 * 1000,
  '/api/products': 30 * 60 * 1000, // 30 minutes
  '/api/flash-games': 60 * 60 * 1000, // 1 hour
  'default': 5 * 60 * 1000 // 5 minutes default
};

function getCacheTTL(url: string): number {
  for (const [pattern, ttl] of Object.entries(API_CACHE_TTL)) {
    if (url.includes(pattern)) {
      return ttl;
    }
  }
  return API_CACHE_TTL.default;
}

export async function getCachedApiResponse<T>(url: string): Promise<T | null> {
  const cacheKey = `api:${url}`;
  return getFromStore<T>(STORES.API_CACHE, cacheKey);
}

export async function setCachedApiResponse<T>(url: string, data: T): Promise<void> {
  const cacheKey = `api:${url}`;
  const ttl = getCacheTTL(url);
  await setInStore(STORES.API_CACHE, cacheKey, data, ttl);
}

export async function invalidateApiCache(urlPattern?: string): Promise<void> {
  if (urlPattern) {
    const cacheKey = `api:${urlPattern}`;
    await deleteFromStore(STORES.API_CACHE, cacheKey);
  } else {
    await clearStore(STORES.API_CACHE);
  }
}

// User data caching
export async function cacheUserData(key: string, data: unknown): Promise<void> {
  await setInStore(STORES.USER_DATA, key, data, 24 * 60 * 60 * 1000);
}

export async function getCachedUserData<T>(key: string): Promise<T | null> {
  return getFromStore<T>(STORES.USER_DATA, key);
}

// Settings caching
export async function cacheSettings(key: string, data: unknown): Promise<void> {
  await setInStore(STORES.SETTINGS, key, data, 7 * 24 * 60 * 60 * 1000); // 7 days
}

export async function getCachedSettings<T>(key: string): Promise<T | null> {
  return getFromStore<T>(STORES.SETTINGS, key);
}

// Preload essential data after login
export async function preloadEssentialData(userType: 'parent' | 'child' | 'admin', token: string): Promise<void> {
  const headers = { Authorization: `Bearer ${token}` };

  const preloadTasks: Array<{ url: string; key: string }> = [
    { url: '/api/subjects', key: '/api/subjects' },
    { url: '/api/seo-settings', key: '/api/seo-settings' },
  ];

  if (userType === 'parent') {
    preloadTasks.push(
      { url: '/api/children', key: '/api/children' },
      { url: '/api/products', key: '/api/products' },
      { url: '/api/parent/wallet', key: '/api/parent/wallet' }
    );
  } else if (userType === 'child') {
    preloadTasks.push(
      { url: '/api/flash-games', key: '/api/flash-games' }
    );
  }

  // Execute preload in parallel
  await Promise.allSettled(
    preloadTasks.map(async ({ url, key }) => {
      try {
        const response = await fetch(url, { headers });
        if (response.ok) {
          const data = await response.json();
          await setCachedApiResponse(key, data);
        }
      } catch (error) {
        console.log(`Preload failed for ${url}:`, error);
      }
    })
  );
}

// Clean up expired cache entries
export async function cleanupExpiredCache(): Promise<void> {
  try {
    const database = await openDB();
    const transaction = database.transaction(STORES.API_CACHE, 'readwrite');
    const store = transaction.objectStore(STORES.API_CACHE);
    const index = store.index('expiresAt');
    const now = Date.now();

    const range = IDBKeyRange.upperBound(now);
    const request = index.openCursor(range);

    request.onsuccess = (event) => {
      const cursor = (event.target as IDBRequest<IDBCursorWithValue>).result;
      if (cursor) {
        cursor.delete();
        cursor.continue();
      }
    };
  } catch (error) {
    console.error('Cache cleanup error:', error);
  }
}

// Initialize cache system
export async function initializeCacheSystem(): Promise<void> {
  try {
    await openDB();
    await cleanupExpiredCache();
  } catch (error) {
    console.error('Failed to initialize cache system:', error);
  }
}

