import { useState, useEffect, useCallback, type ReactNode } from "react";
import { useTranslation } from "react-i18next";
import { Button } from "@/components/ui/button";
import { Download, Smartphone, Apple, Monitor } from "lucide-react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";

interface BeforeInstallPromptEvent extends Event {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
}

interface PWAInstallGateProps {
  children: ReactNode;
}

let deferredPrompt: BeforeInstallPromptEvent | null = null;

export function PWAInstallGate({ children }: PWAInstallGateProps) {
  const { i18n } = useTranslation();
  const { toast } = useToast();
  const isArabic = i18n.language === "ar";

  const [isRunningStandalone, setIsRunningStandalone] = useState(false);
  const [wasInstalled, setWasInstalled] = useState(false);
  const [canInstall, setCanInstall] = useState(false);
  const [showInstallDialog, setShowInstallDialog] = useState(false);
  const [showReinstallDialog, setShowReinstallDialog] = useState(false);
  const [showManualInstructions, setShowManualInstructions] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [installChecked, setInstallChecked] = useState(false);
  const [userDismissed, setUserDismissed] = useState(false);

  const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);
  const isSafari = /^((?!chrome|android).)*safari/i.test(navigator.userAgent);

  useEffect(() => {
    const checkInstallStatus = () => {
      const standaloneCheck = window.matchMedia("(display-mode: standalone)").matches 
        || (window.navigator as any).standalone === true;
      setIsRunningStandalone(standaloneCheck);

      const installedFlag = localStorage.getItem("pwa_installed") === "true";
      setWasInstalled(installedFlag);

      if (deferredPrompt) {
        setCanInstall(true);
      }

      setInstallChecked(true);
    };

    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      deferredPrompt = e as BeforeInstallPromptEvent;
      setCanInstall(true);
    };

    const handleAppInstalled = () => {
      localStorage.setItem("pwa_installed", "true");
      setWasInstalled(true);
      setCanInstall(false);
      setShowInstallDialog(false);
      setShowReinstallDialog(false);
      deferredPrompt = null;
      toast({
        title: isArabic ? "ÿ™ŸÖ ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™ ÿ®ŸÜÿ¨ÿßÿ≠" : "Installed Successfully",
        description: isArabic ? "ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ•ŸÑŸâ ÿ¥ÿßÿ¥ÿ™ŸÉ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©" : "App added to your home screen",
      });
    };

    window.addEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
    window.addEventListener("appinstalled", handleAppInstalled);

    const timer = setTimeout(checkInstallStatus, 100);

    return () => {
      window.removeEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
      window.removeEventListener("appinstalled", handleAppInstalled);
      clearTimeout(timer);
    };
  }, [isArabic, toast]);

  useEffect(() => {
    if (!installChecked || userDismissed) return;

    if (isRunningStandalone) {
      return;
    }

    if (wasInstalled) {
      if (canInstall) {
        setShowReinstallDialog(true);
      }
      return;
    }

    if (!wasInstalled && !isRunningStandalone) {
      const timer = setTimeout(() => {
        setShowInstallDialog(true);
      }, 500);
      return () => clearTimeout(timer);
    }
  }, [installChecked, isRunningStandalone, wasInstalled, canInstall, userDismissed]);

  const triggerInstall = useCallback(async () => {
    if (!deferredPrompt) return false;

    try {
      await deferredPrompt.prompt();
      const result = await deferredPrompt.userChoice;

      if (result.outcome === "accepted") {
        localStorage.setItem("pwa_installed", "true");
        setWasInstalled(true);
        deferredPrompt = null;
        setCanInstall(false);
        return true;
      } else {
        deferredPrompt = null;
        setCanInstall(false);
        return false;
      }
    } catch {
      return false;
    }
  }, []);

  const handleInstallConfirm = useCallback(async () => {
    setIsLoading(true);

    try {
      if (canInstall && deferredPrompt) {
        const installed = await triggerInstall();
        if (installed) {
          setShowInstallDialog(false);
          toast({
            title: isArabic ? "ÿ™ŸÖ ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™" : "Installed",
            description: isArabic ? "ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿ¢ŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "You can now use the app",
          });
        } else {
          setUserDismissed(true);
          setShowInstallDialog(false);
        }
      } else {
        setShowInstallDialog(false);
        setShowManualInstructions(true);
      }
    } finally {
      setIsLoading(false);
    }
  }, [canInstall, triggerInstall, isArabic, toast]);

  const handleReinstallConfirm = useCallback(async () => {
    setIsLoading(true);

    try {
      if (canInstall && deferredPrompt) {
        const installed = await triggerInstall();
        if (installed) {
          setShowReinstallDialog(false);
          toast({
            title: isArabic ? "ÿ™ŸÖÿ™ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™" : "Reinstalled",
            description: isArabic ? "ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ£ŸäŸÇŸàŸÜÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ" : "Icon added back to home screen",
          });
        } else {
          setUserDismissed(true);
          setShowReinstallDialog(false);
        }
      }
    } finally {
      setIsLoading(false);
    }
  }, [canInstall, triggerInstall, isArabic, toast]);

  const handleCancel = useCallback(() => {
    setUserDismissed(true);
    setShowInstallDialog(false);
    setShowReinstallDialog(false);
    setShowManualInstructions(false);
  }, []);

  const handleManualInstallDone = useCallback(() => {
    localStorage.setItem("pwa_installed", "true");
    setWasInstalled(true);
    setShowManualInstructions(false);
    toast({
      title: isArabic ? "ÿ¥ŸÉÿ±ÿßŸã ŸÑŸÉ" : "Thank you",
      description: isArabic ? "ÿßÿ≥ÿ™ŸÖÿ™ÿπ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Enjoy using the app",
    });
  }, [isArabic, toast]);

  return (
    <>
      {children}

      <AlertDialog open={showInstallDialog} onOpenChange={() => {}}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 rounded-2xl bg-primary/10 flex items-center justify-center">
                <Download className="w-8 h-8 text-primary" />
              </div>
            </div>
            <AlertDialogTitle className="text-center text-xl">
              {isArabic ? "ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Install App"}
            </AlertDialogTitle>
            <AlertDialogDescription className="text-center">
              {isArabic 
                ? "ŸÑŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ£ŸÅÿ∂ŸÑ ÿ™ÿ¨ÿ±ÿ®ÿ©ÿå ŸÇŸÖ ÿ®ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿπŸÑŸâ ÿ¨Ÿáÿßÿ≤ŸÉ. ÿ≥Ÿäÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸäŸÇŸàŸÜÿ© ÿ•ŸÑŸâ ÿ¥ÿßÿ¥ÿ™ŸÉ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©." 
                : "For the best experience, install the app on your device. An icon will be added to your home screen."}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter className="flex-col gap-2 sm:flex-row">
            <AlertDialogCancel 
              onClick={handleCancel}
              data-testid="button-cancel-install"
              className="w-full sm:w-auto"
            >
              {isArabic ? "ŸÑŸäÿ≥ ÿßŸÑÿ¢ŸÜ" : "Not Now"}
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleInstallConfirm}
              disabled={isLoading}
              data-testid="button-confirm-install"
              className="w-full sm:w-auto"
            >
              {isLoading 
                ? (isArabic ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™..." : "Installing...") 
                : (isArabic ? "ÿ™ÿ´ÿ®Ÿäÿ™" : "Install")}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <AlertDialog open={showReinstallDialog} onOpenChange={() => {}}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 rounded-2xl bg-orange-500/10 flex items-center justify-center">
                <Smartphone className="w-8 h-8 text-orange-500" />
              </div>
            </div>
            <AlertDialogTitle className="text-center text-xl">
              {isArabic ? "ÿ•ÿπÿßÿØÿ© ÿ™ÿ´ÿ®Ÿäÿ™ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ" : "Reinstall App"}
            </AlertDialogTitle>
            <AlertDialogDescription className="text-center">
              {isArabic 
                ? "Ÿäÿ®ÿØŸà ÿ£ŸÜ ÿ£ŸäŸÇŸàŸÜÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ© ÿπŸÑŸâ ÿ¥ÿßÿ¥ÿ™ŸÉ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©. ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ÿπÿßÿØÿ© ÿ•ÿ∂ÿßŸÅÿ™Ÿáÿßÿü" 
                : "It looks like the app icon is missing from your home screen. Would you like to add it back?"}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter className="flex-col gap-2 sm:flex-row">
            <AlertDialogCancel 
              onClick={handleCancel}
              data-testid="button-cancel-reinstall"
              className="w-full sm:w-auto"
            >
              {isArabic ? "ÿ™ÿÆÿ∑Ÿä" : "Skip"}
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleReinstallConfirm}
              disabled={isLoading}
              data-testid="button-confirm-reinstall"
              className="w-full sm:w-auto"
            >
              {isLoading 
                ? (isArabic ? "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™..." : "Installing...") 
                : (isArabic ? "ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™" : "Reinstall")}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <AlertDialog open={showManualInstructions} onOpenChange={() => {}}>
        <AlertDialogContent className="max-w-md">
          <AlertDialogHeader>
            <div className="flex justify-center mb-4">
              {isIOS ? (
                <div className="w-16 h-16 rounded-2xl bg-blue-500/10 flex items-center justify-center">
                  <Apple className="w-8 h-8 text-blue-500" />
                </div>
              ) : (
                <div className="w-16 h-16 rounded-2xl bg-green-500/10 flex items-center justify-center">
                  <Monitor className="w-8 h-8 text-green-500" />
                </div>
              )}
            </div>
            <AlertDialogTitle className="text-center text-xl">
              {isArabic ? "ÿ™ÿ´ÿ®Ÿäÿ™ ŸäÿØŸàŸä" : "Manual Installation"}
            </AlertDialogTitle>
            <AlertDialogDescription className="text-center space-y-3">
              {isIOS && isSafari ? (
                <div className="text-right" dir="rtl">
                  {isArabic ? (
                    <ol className="list-decimal list-inside space-y-2 text-sm">
                      <li>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ£ŸäŸÇŸàŸÜÿ© ÿßŸÑŸÖÿ¥ÿßÿ±ŸÉÿ© (ÿßŸÑŸÖÿ±ÿ®ÿπ ŸÖÿπ ÿßŸÑÿ≥ŸáŸÖ)</li>
                      <li>ŸÖÿ±ÿ± ŸÑŸÑÿ£ÿ≥ŸÅŸÑ ŸàÿßÿÆÿ™ÿ± "ÿ•ÿ∂ÿßŸÅÿ© ÿ•ŸÑŸâ ÿßŸÑÿ¥ÿßÿ¥ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©"</li>
                      <li>ÿßÿ∂ÿ∫ÿ∑ "ÿ•ÿ∂ÿßŸÅÿ©" ŸÅŸä ÿßŸÑÿ≤ÿßŸàŸäÿ© ÿßŸÑÿπŸÑŸàŸäÿ©</li>
                    </ol>
                  ) : (
                    <ol className="list-decimal list-inside space-y-2 text-sm text-left" dir="ltr">
