            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter className="flex-col gap-2 sm:flex-row">
            <AlertDialogCancel 
              onClick={handleCancel}
              data-testid="button-cancel-manual"
              className="w-full sm:w-auto"
            >
              {isArabic ? "ÿ™ÿÆÿ∑Ÿä" : "Skip"}
            </AlertDialogCancel>
            <Button 
              onClick={handleManualInstallDone}
              data-testid="button-done-manual"
              className="w-full sm:w-auto"
            >
              {isArabic ? "ÿ™ŸÖ ÿßŸÑÿ™ÿ´ÿ®Ÿäÿ™" : "Done Installing"}
            </Button>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

