  <script>
    const session = sessionStorage.getItem('adminSession');
    if (!session) {
      window.location.href = '/admin/env/login';
    }

    async function loadEnv() {
      try {
        const res = await fetch('/admin/env', {
          headers: { 'x-admin-session': session }
        });
        if (!res.ok) {
          if (res.status === 401) {
            sessionStorage.removeItem('adminSession');
            window.location.href = '/admin/env/login';
            return;
          }
          throw new Error('Failed to load');
        }
        const data = await res.json();

        Object.entries(data.env).forEach(([key, value]) => {
          const input = document.querySelector('[name="' + key + '"]');
          if (input && !String(value).includes('[CONFIGURED]')) {
            input.value = value;
          }
        });

        document.getElementById('loading').style.display = 'none';
        document.getElementById('main').style.display = 'block';
      } catch (e) {
        document.getElementById('loading').textContent = 'ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™';
      }
    }

    document.getElementById('envForm').addEventListener('submit', async (e) => {
      e.preventDefault();
      const btn = document.getElementById('saveBtn');
      btn.disabled = true;
      btn.textContent = 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...';

      const formData = new FormData(e.target);
      const data = {};
      formData.forEach((value, key) => {
        if (value && String(value).trim()) data[key] = String(value).trim();
      });

      try {
        const res = await fetch('/admin/env', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'x-admin-session': session
          },
          body: JSON.stringify(data)
        });

        const result = await res.json();
        if (!res.ok) throw new Error(result.message || 'Failed');

        let successMsg = '=== ÿ™ŸÖ ÿßŸÑÿ≠ŸÅÿ∏ ÿ®ŸÜÿ¨ÿßÿ≠ ===\\n\\n';
        successMsg += '‚úÖ ' + (result.message || 'ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠') + '\\n\\n';
        if (result.updatedKeys && result.updatedKeys.length > 0) {
          successMsg += 'üìù ÿßŸÑŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖÿ≠ÿØÿ´ÿ©:\\n';
          result.updatedKeys.forEach(key => {
            successMsg += '   ‚Ä¢ ' + key + '\\n';
          });
        }
        if (result.note) {
          successMsg += '\\n‚ö†Ô∏è ' + result.note;
        }
        showToolResult(successMsg);
        showAlert('ÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠!', 'success');
      } catch (e) {
        showToolResult('=== ŸÅÿ¥ŸÑ ÿßŸÑÿ≠ŸÅÿ∏ ===\\n\\n‚ùå ' + (e.message || 'ŸÅÿ¥ŸÑ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™'), true);
        showAlert(e.message || 'ŸÅÿ¥ŸÑ ÿ≠ŸÅÿ∏ ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™', 'error');
      } finally {
        btn.disabled = false;
        btn.textContent = 'ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±ÿßÿ™';
      }
    });

    function showAlert(msg, type) {
      const alert = document.getElementById('alert');
      alert.className = 'alert alert-' + type;
      alert.textContent = msg;
      setTimeout(() => alert.textContent = '', 5000);
    }

    function logout() {
      fetch('/admin/env/logout', {
        method: 'POST',
        headers: { 'x-admin-session': session }
      }).finally(() => {
        sessionStorage.removeItem('adminSession');
        window.location.href = '/admin/env/login';
      });
    }

    function showToolResult(content, isError = false) {
      const result = document.getElementById('toolResult');
      result.className = 'tool-result' + (isError ? ' error' : '');
      result.innerHTML = '<button class="close-btn" onclick="hideToolResult()">ÿ•ÿ∫ŸÑÿßŸÇ</button><br>' + content;
      result.style.display = 'block';
      result.scrollTop = 0;
    }

    function hideToolResult() {
      document.getElementById('toolResult').style.display = 'none';
    }

    async function checkHealth() {
      try {
        const res = await fetch('/api/health');
        const data = await res.json();
        showToolResult('=== ŸÅÿ≠ÿµ ÿµÿ≠ÿ© ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ± ===\\n' + JSON.stringify(data, null, 2));
      } catch (e) {
        showToolResult('ŸÅÿ¥ŸÑ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±: ' + e.message, true);
      }
    }

    async function viewLogs() {
      try {
        const res = await fetch('/admin/logs', {
          headers: { 'x-admin-session': session }
        });
