import { describe, it, beforeAll, expect } from "@jest/globals";
import request from "supertest";
import express, { Request, Response, NextFunction } from "express";
import { ZodError, z } from "zod";

describe("Error Handling", () => {
  let app: express.Application;

  const errorHandler = (err: any, _req: Request, res: Response, _next: NextFunction) => {
    if (err instanceof ZodError) {
      return res.status(400).json({
        success: false,
        error: "Validation failed",
        details: err.errors,
      });
    }

    if (err.statusCode) {
      return res.status(err.statusCode).json({
        success: false,
        error: err.message,
      });
    }

    return res.status(500).json({
      success: false,
      error: "Internal server error",
    });
  };

  beforeAll(() => {
    app = express();
    app.use(express.json());

    app.post("/api/validate", (req, _res, next) => {
      try {
        const schema = z.object({
          email: z.string().email(),
          password: z.string().min(6),
        });
        schema.parse(req.body);
        next();
      } catch (err) {
        next(err);
      }
    }, (_req, res) => {
      res.json({ success: true });
    });

    app.get("/api/not-found", (_req, _res, next) => {
      const error: any = new Error("Resource not found");
      error.statusCode = 404;
      next(error);
    });

    app.get("/api/server-error", (_req, _res, next) => {
      next(new Error("Something went wrong"));
    });

    app.use(errorHandler);
  });

  describe("Validation Errors", () => {
    it("should return 400 for invalid email", async () => {
      const response = await request(app)
        .post("/api/validate")
        .send({ email: "invalid", password: "password123" })
        .expect(400);

      expect(response.body).toHaveProperty("success", false);
      expect(response.body).toHaveProperty("error", "Validation failed");
      expect(response.body).toHaveProperty("details");
    });

    it("should return 400 for short password", async () => {
      const response = await request(app)
        .post("/api/validate")
        .send({ email: "test@example.com", password: "123" })
        .expect(400);

      expect(response.body).toHaveProperty("success", false);
    });

    it("should pass validation with valid data", async () => {
      const response = await request(app)
        .post("/api/validate")
        .send({ email: "test@example.com", password: "password123" })
        .expect(200);

      expect(response.body).toHaveProperty("success", true);
    });
  });

  describe("Not Found Errors", () => {
    it("should return 404 with proper message", async () => {
      const response = await request(app)
        .get("/api/not-found")
        .expect(404);

      expect(response.body).toHaveProperty("success", false);
      expect(response.body).toHaveProperty("error", "Resource not found");
    });
  });

  describe("Server Errors", () => {
    it("should return 500 for unhandled errors", async () => {
      const response = await request(app)
        .get("/api/server-error")
        .expect(500);

      expect(response.body).toHaveProperty("success", false);
      expect(response.body).toHaveProperty("error", "Internal server error");
