import { describe, it, beforeAll, expect } from "@jest/globals";
import request from "supertest";
import express from "express";
import jwt from "jsonwebtoken";

const JWT_SECRET = "test-secret-key";

describe("Authentication", () => {
  let app: express.Application;

  const authMiddleware = (req: any, res: any, next: any) => {
    const authHeader = req.headers.authorization;
    if (!authHeader || !authHeader.startsWith("Bearer ")) {
      return res.status(401).json({ error: "Unauthorized" });
    }

    const token = authHeader.split(" ")[1];
    try {
      const decoded = jwt.verify(token, JWT_SECRET);
      req.user = decoded;
      next();
    } catch {
      return res.status(401).json({ error: "Invalid token" });
    }
  };

  beforeAll(() => {
    app = express();
    app.use(express.json());

    app.get("/api/protected", authMiddleware, (req: any, res) => {
      res.json({
        message: "Access granted",
        userId: req.user.userId,
      });
    });

    app.get("/api/public", (_req, res) => {
      res.json({ message: "Public endpoint" });
    });
  });

  describe("Protected Route", () => {
    it("should reject request without token", async () => {
      const response = await request(app)
        .get("/api/protected")
        .expect(401);

      expect(response.body).toHaveProperty("error", "Unauthorized");
    });

    it("should reject request with invalid token", async () => {
      const response = await request(app)
        .get("/api/protected")
        .set("Authorization", "Bearer invalid-token")
        .expect(401);

      expect(response.body).toHaveProperty("error", "Invalid token");
    });

    it("should accept request with valid token", async () => {
      const token = jwt.sign({ userId: "test-user-123", type: "parent" }, JWT_SECRET);

      const response = await request(app)
        .get("/api/protected")
        .set("Authorization", `Bearer ${token}`)
        .expect(200);

      expect(response.body).toHaveProperty("message", "Access granted");
      expect(response.body).toHaveProperty("userId", "test-user-123");
    });

    it("should reject expired token", async () => {
      const token = jwt.sign(
        { userId: "test-user-123", type: "parent" },
        JWT_SECRET,
        { expiresIn: "-1h" }
      );

      const response = await request(app)
        .get("/api/protected")
        .set("Authorization", `Bearer ${token}`)
        .expect(401);

      expect(response.body).toHaveProperty("error", "Invalid token");
    });
  });

  describe("Public Route", () => {
    it("should allow access without token", async () => {
      const response = await request(app)
        .get("/api/public")
        .expect(200);

      expect(response.body).toHaveProperty("message", "Public endpoint");
    });
  });
});

